import React, {useState, useEffect} from 'react';
import {View, Button, Image, Text } from 'react-native';

export default function Pergunta({route, navigation}){
    var score = route.params;
    const [foto, setFoto] = useState(require("../../assets/narutof.png"));
    const [msg, setMsg] = useState("");

    useEffect(()=>{
        console.log(score)
        if(score <= 2){
            setFoto("../../assets/narutot.jpg")
            setMsg("Você decepcionou o Naruto")
        }else{
            setFoto("../../assets/narutof.png")
            setMsg("Parabéns, Naruto está orgulhoso!!")
        }
    }, []);


    return(
        <View>
            <Image source={foto} style={{width: 100, height: 100}}/>
            <View>
                <Text >{msg}</Text>
            </View>
            <View>
                <Button title="Jogar Novamente"  onPress={() => {navigation.navigate('Home')}}/>
            </View>
        </View>
    );

}
