import React from 'react';
import {View, Button, Image, Text, ToastAndroid, StatusBar } from 'react-native';
import style from '../../../proj02/pages/quiz1/style';

export default function Pergunta({route, navigation}){
    const score = 0;

    const styles = StyleSheet.create({
        container: {
          flex: 1,
          justifyContent: 'center',
          paddingTop: StatusBar.currentHeight,
          backgroundColor: '#6638f0',
          padding: 8,
        },
      });

    function showToast() {
        ToastAndroid.show('Request sent successfully!', ToastAndroid.SHORT);
      }

    return(
        <View>
            <Image source={require("../../assets/orochi.jpg")} style={style.img}/>
            <Image source={require("../../assets/orochi1.png")} style={{width: '100vw', height: '20vh'}}/>
            <View>
                <Text>Quem Ã© este personagem?</Text>
            </View>
            <View style={styles.container}>
                <Button title="Rock Lee"/>
                <Button title="Guy"/>
                <Button title="Naruto"/>
                <Button title="Orochimaru"/>
                <Button title="TESTE" onPress={showToast}/>
            </View>
        </View>
    );
    
}
