import React from 'react';
import {View, Button, Image, Text } from 'react-native';
import style from '../../../proj02/pages/quiz1/style';

import Toast from 'react-native-tiny-toast'

const toastError= msg => Toast.show(msg,{
    position: Toast.position.center,
    containerStyle:{
     backgroundColor: '#f00',
     borderRadius: 15,
    },
    textStyle:{
     color:'#fff',
    },
    imgStyle:{},
    mask:false,
    maskStyle:{},
    duration: 2000,
    animation: true,
});

export default function Pergunta({route, navigation}){
    const score = 0;

    function showToast() {
        toastError('Quantidade solicitada não disponível!');
    };

    return(
        <View>
            <Image source={require("../../assets/orochi.jpg")} style={style.img}/>
            <Image source={require("../../assets/orochi1.png")} style={{width: '100vw', height: '20vh'}}/>
            <View>
                <Text>Quem é este personagem?</Text>
            </View>
            <View>
                <Button title="Rock Lee"/>
                <Button title="Guy"/>
                <Button title="Naruto"/>
                <Button title="Orochimaru"/>
                <Button title="TESTE" onPress={showToast}/>
            </View>
        </View>
    );
}
