import React, {useState, useEffect} from 'react';
import {View, TouchableOpacity, Text, TextInput} from 'react-native';

export default function Criacao() {
    const [equipe, setEquipe] = useState("");
    const [j1, setJ1] = useState("");
    const [j2, setJ2] = useState("");
    const [j3, setJ3] = useState("");
    const [j4, setJ4] = useState("");
    const [j5, setJ5] = useState("");
    const [coach, setCoach] = useState("");
    const [msg, setMsg] = useState("");

    const criar = (navigation) => {
        let url = "http://10.87.207.20:8080/api/equipe";

        let time = {
            "nomeEquipe": equipe,
            "jogadores": [
                {
                    "posicao": "top",
                    "jogador": j1
                },
                {
                    "posicao": "mid",
                    "jogador": j2
                },
                {
                    "posicao": "jg",
                    "jogador": j3
                },
                {
                    "posicao": "adc",
                    "jogador": j4
                },
                {
                    "posicao": "sup",
                    "jogador": j5
                }
            ],
            "nomeCoach": coach
        }

        fetch(url, {
            method: "POST",
            body: JSON.stringify(time)
        }).then(resp => {
            
            if(resp.status == 200){
                return resp.json();
            }else{
                setMsg("Preencha todos os campos")
            }
        }).then(data =>{
            if(data != undefined){
                navigation.navigate("Home", data);
            }
        }).then(err =>{
            console.log(err)
        })
    }

    const teste = () => {
        console.log(j1)
    }


    return(
        <View>
            <Text>Criação de Equipe</Text>
            <TextInput placeholder="Nome da Equipe" onChangeText={(value) => {setEquipe(value)}} />
            <View>
                <Text>Jogadores:</Text>
                <View>
                    <View>
                        <Text>Jogador Top</Text>
                        <TextInput placeholder="Nome do Jogador" onChangeText={(value) => {setJ1(value)}} />
                    </View>
                    <View>
                        <Text>Jogador Mid</Text>
                        <TextInput placeholder="Nome do Jogador" onChangeText={(value) => {setJ2(value)}} />
                    </View>
                    <View>
                        <Text>Jogador Jungler</Text>
                        <TextInput placeholder="Nome do Jogador" onChangeText={(value) => {setJ3(value)}} />
                    </View>
                    <View>
                        <Text>Jogador ADC</Text>
                        <TextInput placeholder="Nome do Jogador" onChangeText={(value) => {setJ4(value)}} />
                    </View>
                    <View>
                        <Text>Jogador Suporte</Text>
                        <TextInput placeholder="Nome do Jogador" onChangeText={(value) => {setJ5(value)}} />
                    </View>
                </View>
            </View>
            <TextInput placeholder="Nome do Coach" onChangeText={(value) => {setCoach(value)}} />
            <Text>{msg}</Text>
            <TouchableOpacity onPress={criar}>
                <Text>Salvar</Text>
            </TouchableOpacity>
            {console.log(j1)}
        </View>
    )
}
